function onSubmit() {
  // Prevent default submission so you can show message
  var form = this;

  // Call server to submit the record producer (using GlideRecord insert or via server script)
  form.server.submit().then(function(response) {
    // Show a toast or modal success message
    spModal.alert("Request was submitted successfully!").then(function() {
      // Redirect or close modal after user clicks OK
      window.location.href = '/pam';  // Or wherever you want to go
    });
  });

  return false; // prevent default submit
}
