sp_widget.client_script:
api.controller=function($scope, $http) {
  var c = this;

  c.create = function() {
    // Redirect to a form or modal for creation
    // window.location.href = '/x_1734342_pletra_0_new_asset_request.do';
	window.location.href = '/sp?id=sc_cat_item&sys_id=212c152583e56e50f257f3a6feaad3d1';

  };

   c.view = function(asset) {
    if (c.data.isHR) {
      // Open in view-only mode
      window.location.href = '/x_1734342_pletra_0_new_asset_request.do?sys_id=' + asset.sys_id + '&sysparm_view=default&sysparm_readonly=true';
    } else {
      // Open in editable mode
      window.location.href = '/x_1734342_pletra_0_new_asset_request.do?sys_id=' + asset.sys_id;
    }
  };
};

