sp_widget.script:
(function() {
	data.isHR = gs.hasRole('x_1734342_pletra_0.hr'); 
    var gr = new GlideRecord('x_1734342_pletra_0_new_asset_request');
    gr.query();

    var assets = [];
    while (gr.next()) {
        assets.push({
            sys_id: gr.getValue('sys_id'),
            assigned_to: gr.getDisplayValue('requested_for'),
            location: gr.getDisplayValue('location'),
			joiningDate: gr.getDisplayValue('date_of_joining'),
			department: gr.getDisplayValue('department'),
			status: gr.getDisplayValue('status')
        });
    }

    data.assets = assets;
})();
