sp_widget.client_script:
api.controller = function() {
  var c = this;

  // List of tables to display
  c.tables = [
    { label: 'Centralized Assets', name: 'x_1734342_pletra_0_centralized_asset_table' },
    { label: 'Capital Assets', name: 'x_1734342_pletra_0_capital_asset' },
    { label: 'Commodity Assets', name: 'x_1734342_pletra_0_commodity_asset' },
    { label: 'New Hire Asset Requests', name: 'x_1734342_pletra_0_new_asset_request' }
  ];

  // Returns standard UI list URL
  c.getLink = function(table) {
    return `/${table.name}_list.do`;
  };
};

