sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    var attachmentGR = new GlideRecord('sys_attachment');
    attachmentGR.addQuery('file_name', 'Asset Handover Form_0.1.docx');  // Change to your actual PDF file name
    attachmentGR.query();

    if (attachmentGR.next()) {
        var mail = new GlideEmailOutbound();
        mail.setSubject("Test Email with Static PDF Attachment");
        mail.setBody("Hello,\n\nPlease find the attached PDF document.");

        var userGR = new GlideRecord('sys_user');
        if (userGR.get(current.assigned_to)) {
            mail.addAddress("to", userGR.email);
        } else {
            mail.addAddress("to", 'sneha.kashid@pletratech.com');  // Fallback email
        }

        mail.setFrom("sneha.kashid@pletratech.com");
        mail.setTable(current.getTableName(), current.sys_id);

        // Attach the existing PDF file to the email
        mail.addAttachment(attachmentGR.sys_id);

        mail.send();

        gs.info("Email sent with static PDF attachment.");
    } else {
        gs.error("Static PDF attachment not found.");
    }
})(current, previous);
