sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') {
        return;
    }

    console.log('onChange Triggered. New Employee ID: ' + newValue);

    var ga = new GlideAjax('GetEmployeeDetails');
    ga.addParam('sysparm_name', 'getEmployeeName');
    ga.addParam('sysparm_emp_id', newValue);
    ga.getXMLAnswer(function(response) {
        console.log('GlideAjax Response: ' + response);

        if (response) {
            g_form.setValue('employee_name', response);
        } else {
            g_form.clearValue('employee_name');
        }
    });
}
