sys_script_include.script:
var GetEmployeeDetails = Class.create();
GetEmployeeDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getEmployeeName: function() {
        var empId = this.getParameter('sysparm_emp_id');
        gs.info('DEBUG: Received emp_id: ' + empId);

        var gr = new GlideRecord('x_1734342_pletra_0_centralized_asset_table');
        gr.addQuery('emp_id', empId);
        gr.query();

        if (gr.next()) {
            var empName = gr.getDisplayValue('assigned_to'); // Gets display value
            gs.info('DEBUG: Found employee: ' + empName);
            return empName;
        } else {
            gs.info('DEBUG: No record found for emp_id: ' + empId);
        }
        return '';
    }
});
